import React, { useEffect, useState } from "react";
import axios from "axios";
import io from "socket.io-client";
import { FaStar } from "react-icons/fa";

const socket = io("http://localhost:5000");

const FeedbackList = () => {
  const [feedbacks, setFeedbacks] = useState([]);

  // Load initial feedbacks
  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/feedback");
        setFeedbacks(res.data);
      } catch (err) {
        console.error("Error fetching feedbacks", err);
      }
    };
    fetchData();
  }, []);

  // Socket.IO live updates
  useEffect(() => {
    socket.on("newFeedback", (data) => {
      setFeedbacks((prev) => [data, ...prev]);
    });

    socket.on("deleteFeedback", (id) => {
      setFeedbacks((prev) => prev.filter((fb) => fb._id !== id));
    });

    return () => {
      socket.off("newFeedback");
      socket.off("deleteFeedback");
    };
  }, []);

  const handleDelete = async (id) => {
    const confirmDelete = window.confirm("‚ö†Ô∏è Are you sure you want to delete this feedback?");
    if (!confirmDelete) return;

    try {
      await axios.delete(`http://localhost:5000/api/feedback/${id}`);
      // frontend update handled by socket event
    } catch (err) {
      console.error(err);
      alert("Error deleting feedback");
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">üí¨ Live Feedbacks</h2>
      {feedbacks.length === 0 ? (
        <p className="text-center text-muted">No feedback yet. Be the first!</p>
      ) : (
        <div className="row">
          {feedbacks.map((fb) => (
            <div className="col-md-6 mb-3" key={fb._id}>
              <div className="card shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">
                    {fb.name}{" "}
                    <small className="text-muted" style={{ fontSize: "0.8rem" }}>
                      ({fb.email})
                    </small>
                  </h5>
                  <p className="card-text">{fb.message}</p>
                  <p className="text-muted" style={{ fontSize: "0.8rem" }}>
                    {new Date(fb.createdAt).toLocaleString()}
                  </p>

                  {/* Star rating display */}
                  <div className="mb-2">
                    {[...Array(5)].map((star, index) => {
                      const ratingValue = index + 1;
                      return (
                        <FaStar
                          key={index}
                          size={20}
                          color={ratingValue <= fb.rating ? "#ffc107" : "#e4e5e9"}
                        />
                      );
                    })}
                  </div>

                  <button
                    className="btn btn-sm btn-danger"
                    onClick={() => handleDelete(fb._id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FeedbackList;
