import React, { useEffect, useState } from "react";
import axios from "axios";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

const FeedbackList = () => {
  const [feedbacks, setFeedbacks] = useState([]);

  // Load initial feedbacks
  useEffect(() => {
    const fetchData = async () => {
      const res = await axios.get("http://localhost:5000/api/feedback");
      setFeedbacks(res.data);
    };
    fetchData();
  }, []);

  // Listen for new feedback via Socket.IO
  useEffect(() => {
    socket.on("newFeedback", data => {
      setFeedbacks(prev => [data, ...prev]);
    });
    return () => socket.off("newFeedback");
  }, []);

  return (
    <div>
      <h2>Live Feedbacks</h2>
      {feedbacks.map(fb => (
        <div key={fb._id} style={{ border: "1px solid #ccc", margin: "5px", padding: "5px" }}>
          <p><strong>{fb.name}</strong> ({fb.email})</p>
          <p>{fb.message}</p>
        </div>
      ))}
    </div>
  );
};

export default FeedbackList;
